package com.mazanov.rick_and_morty.data.local_data_source.daoimport androidx.room.*import com.mazanov.rick_and_morty.data.local_data_source.entitys.PersonModelEntity@Daointerface PersonDao {    @Query("SELECT * FROM persons")     suspend fun getAllPersonFromDb(): List<PersonModelEntity>    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend  fun addPersonToDb(personModelEntity : PersonModelEntity)    @Query("DELETE FROM persons WHERE id =:personId")    suspend fun deletePersonFromDb(personId:Int)    @Query("SELECT * FROM persons WHERE id =:personId LIMIT 1")    suspend fun getOnePersonFromDb(personId:Int): PersonModelEntity}