package com.mazanov.rick_and_morty.data.local_data_source.entitysimport androidx.room.Embeddedimport androidx.room.Entityimport androidx.room.PrimaryKey@Entity(tableName = "persons")data class PersonModelEntity(    @PrimaryKey    val id: Int,    val created: String,    val episode: List<String>,    val image: String,    @Embedded(prefix ="location" )    val location: LocationEntity,    val name: String,    @Embedded(prefix ="origin" )    val origin: OriginEntity,    val species: String,    val gender: String,    val status: String,    val url: String)