package com.mazanov.rick_and_morty.data.remote_data_sourceimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport okhttp3.OkHttpClientimport okhttp3.logging.HttpLoggingInterceptorimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)object RetrofitInstance {    private val okHttpClient = OkHttpClient.Builder()        .addInterceptor(HttpLoggingInterceptor().apply {            level = HttpLoggingInterceptor.Level.BODY        })        .build()    @Provides    @Singleton    fun retrofit(): ApiService = Retrofit.Builder()        .baseUrl("https://rickandmortyapi.com/?page=5")        .addConverterFactory(GsonConverterFactory.create())        .client(okHttpClient)        .build()        .create(ApiService::class.java)}