package com.mazanov.rick_and_morty.data.remote_data_source.mapperimport com.mazanov.rick_and_morty.data.remote_data_source.dto.LocationDtoimport com.mazanov.rick_and_morty.data.remote_data_source.dto.OriginDtoimport com.mazanov.rick_and_morty.data.remote_data_source.dto.PersonModelDtoimport com.mazanov.rick_and_morty.data.remote_data_source.dto.ResultDtoimport com.mazanov.rick_and_morty.domain.models.Locationimport com.mazanov.rick_and_morty.domain.models.Originimport com.mazanov.rick_and_morty.domain.models.PersonModelimport com.mazanov.rick_and_morty.domain.models.ResultFromApiimport javax.inject.Injectclass PersonMapper @Inject constructor() {    fun mapResultDtoToResultFromApi(response: ResultDto) : ResultFromApi {        return ResultFromApi( personList = mapResponseToListPersonModel(response.results))    }    private fun mapResponseToListPersonModel(response: List<PersonModelDto>): List<PersonModel> {        val result = mutableListOf<PersonModel>()        for (person in response) {            val personModel = PersonModel(                created = person.created,                episode = person.episode,                id = person.id,                image = person.image,                location = mapResponseToLocationDto(person.location),                name = person.name,                origin = mapResponseToOriginDto(person.origin),                species = person.species,                status = person.status,                url = person.url,                gender = person.gender            )            result.add(personModel)        }        return result    }}private  fun mapResponseToLocationDto(locationDto: LocationDto): Location {    return Location(        name = locationDto.name,        url = locationDto.url    )}private fun mapResponseToOriginDto(originDto: OriginDto): Origin {    return Origin(        name = originDto.name,        url = originDto.url    )}