package com.mazanov.rick_and_morty.data.repository_Implementationimport com.mazanov.rick_and_morty.data.mappers.PersonMapperimport com.mazanov.rick_and_morty.data.remote_data_source.RetrofitInstanceimport com.mazanov.rick_and_morty.domain.models.PersonModelimport com.mazanov.rick_and_morty.domain.repository.Repositoryclass RepositoryImpl : Repository {    private val apiService = RetrofitInstance.apiService    private val mapper = PersonMapper()    private val _persons = mutableListOf<PersonModel>()    private val persons: List<PersonModel>        get() = _persons.toList()    override suspend fun getAllCharacters(page:Int): List<PersonModel> {        val response = apiService.getAllCharactersFromApi(page)        val personModelList = mapper.mapResultDtoToResultFromApi(response)        _persons.addAll(personModelList.personList)        return _persons    }}