package com.mazanov.rick_and_morty.data.repository_Implementationimport com.mazanov.rick_and_morty.data.mappers.PersonMapperimport com.mazanov.rick_and_morty.data.remote_data_source.ApiServiceimport com.mazanov.rick_and_morty.domain.models.PersonModelimport com.mazanov.rick_and_morty.domain.repository.Repositoryimport javax.inject.Injectclass RepositoryImpl @Inject constructor(    private val mapper:PersonMapper,    private val apiService: ApiService) : Repository {    private val _persons = mutableListOf<PersonModel>()    private val persons: List<PersonModel>        get() = _persons.toList()    override suspend fun getAllCharacters(page:Int): List<PersonModel> {        val response = apiService.getAllCharactersFromApi(page)        val personModelList = mapper.mapResultDtoToResultFromApi(response)        _persons.addAll(personModelList.personList)        return _persons    }}