package com.mazanov.rick_and_morty.domain.modelsdata class PersonModel(    val created: String,    val episode: List<String>,    val id: Int,    val image: String,    val location: Location,    val name: String,    val origin: Origin,    val species: String,    val gender: String,    val status: String,    val url: String) {    fun doesMatchSearchQuery(query: String): Boolean {        val matchingCombinations = listOf(            name,            "${name.first()}",            name.take(2),            name.take(3),            "${name.first()}${name.take(2)}${name.take(3)}",        )        return matchingCombinations.any {            it.trim().contains(query, ignoreCase = true)        }    }    companion object {        const val TOTAL_PAGES = 42    }}