package com.mazanov.rick_and_morty.presentation.navigationimport androidx.compose.runtime.Composableimport androidx.navigation.NavHostControllerimport androidx.navigation.NavTypeimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.navArgumentimport com.google.gson.Gsonimport com.mazanov.rick_and_morty.domain.models.PersonModel@Composablefun AppNavGraph(    navHostController: NavHostController,    personScreenContent: @Composable (person: PersonModel) -> Unit,    mainScreenContent: @Composable () -> Unit) {    NavHost(        navController = navHostController,        startDestination = Screen.Main.rout    ) {        composable(            route = Screen.Person.rout,            arguments = listOf(                navArgument(Screen.KEY_PERSON_ITEM) {                    type = NavType.StringType                }            )        ) {            val personFromJson = it.arguments?.getString(Screen.KEY_PERSON_ITEM) ?: ""            val person = Gson().fromJson(personFromJson, PersonModel::class.java)            personScreenContent(person)        }        composable(Screen.Main.rout) {            mainScreenContent()        }    }}