package com.mazanov.rick_and_morty.presentation.navigationimport android.net.Uriimport com.google.gson.Gsonimport com.mazanov.rick_and_morty.domain.models.PersonModelsealed class Screen(    val rout: String) {    object Home : Screen(ROUT_HOME)    object Favourite : Screen(ROUTE_FAVOURITE)    object Person : Screen(PERSON_ROUT) {        private const val ROUTE_FOR_ARGS = "person"        fun getRouteWithArgs(person: PersonModel): String {            val personJson = Gson().toJson(person)            return "$ROUTE_FOR_ARGS/${personJson.encode()}"        }    }    companion object {        const val KEY_PERSON_ITEM = "person_item_key"        const val ROUT_HOME = "home"        const val ROUTE_FAVOURITE = "favourite"        const val PERSON_ROUT = "person/{$KEY_PERSON_ITEM}"    }}//fun String.encode(): String {    return Uri.encode(this)}