package com.mazanov.rick_and_morty.presentation.screens.fovourite_screenimport androidx.compose.foundation.layout.*import androidx.compose.material.*import androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.ArrowBackimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport com.mazanov.rick_and_morty.presentation.navigation.NavigationState@Composablefun FavouriteScreen(    navigationState:NavigationState) {    Scaffold(        topBar = {            TopAppBar(                backgroundColor = MaterialTheme.colors.surface,                modifier = Modifier.height(55.dp),                elevation = 0.dp            ) {                IconButton(onClick = { navigationState.navHostController.popBackStack() }) {                    Icon(                        imageVector = Icons.Filled.ArrowBack,                        contentDescription = "Back",                        tint = Color.LightGray                    )                }                Spacer(modifier = Modifier.width(10.dp))                Text(                    text = "Character",                    fontSize = 20.sp,                    color = Color.LightGray                )            }        }    ) {        Box(modifier = Modifier            .fillMaxSize()            .padding(it), Alignment.Center) {            Text(text = "Favourite Screen")        }    }}