package com.mazanov.rick_and_morty.presentation.screens.main_screenimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.outlined.Favoriteimport androidx.compose.material.icons.outlined.Homeimport androidx.compose.ui.graphics.vector.ImageVectorimport com.mazanov.rick_and_morty.Rimport com.mazanov.rick_and_morty.presentation.navigation.Screensealed class NavigationItem(    val screen: Screen,    val icon: ImageVector,    val title: Int) {    object Home: NavigationItem(        screen = Screen.Home,        icon = Icons.Outlined.Home,        title = R.string.navigation_item_home    )    object Favourite : NavigationItem(        screen = Screen.Favourite,        icon = Icons.Outlined.Favorite,        title = R.string.navigation_item_favorite    )}